import React, { useState } from 'react';
import SvgViewer from '../../components/initialAnnimation/svgViewer';
import LandingPage from '../landingPage/landingPage';

function SplashPage() {
    const[viewToShow, set_viewToShow]=useState('svgViewer');

    function changeView(view){
        switch(view) {
            case 'svgViewer':
                set_viewToShow("svgViewer");
                break;
            case 'landingPage':
                set_viewToShow("landingPage");
                break;
            default:
                return null;
        }
    }

    function showView() {
        switch(viewToShow) {
            case 'svgViewer':
                  return  <SvgViewer />   

            case 'landingPage':
                  return <LandingPage />

            default:
                return <SvgViewer />

        }
    }

    return (
        <div>

{showView()}
        <button onClick={()=>changeView("landingPage")}
        >change to landingPage</button>

        </div>
    )
} 
export default SplashPage
