import React, { useState } from 'react';
import SvgViewer from '../../components/initialAnnimation/svgViewer';
import LandingPage from '../landingPage/landingPage';

function SplashPage() {
    let[viewToShow, set_viewToShow]=useState('svgViewer');

    function changeView(){
        console.log(viewToShow)
       if(viewToShow="svgViewer") {
        set_viewToShow("landingPage")

       }

       else if (viewToShow="landingPage") {
        set_viewToShow("svgViewer")

       }
    }

    function showView() {
        switch(viewToShow) {
            case 'svgViewer':
                  return  <SvgViewer />   

            case 'landingPage':
                  return <LandingPage />

            default:
                return <SvgViewer />

        }
    }

    return (
        <div>

{showView()}
        <button onClick={()=>changeView()}
        >change to landingPage</button>

        </div>
    )
} 
export default SplashPage
